// App.js

import React, { useState } from 'react';
import Sidebar from './Sidebar';
import Header from './Header';
import PolicyTable from './PolicyTable';
import ArchiveTable from './ArchiveTable';
import UserTable from './UserTable';
import RoleTable from './RoleTable';
import RejectedPoliciesTable from './RejectedPoliciesTable';
import Login from './Login'; // Assuming you have a Login component
import '../Style/App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activeTable, setActiveTable] = useState('PolicyTable');

  // Function to handle login status
  const handleLogin = (status) => {
    setIsLoggedIn(status);
  };

  // Function to handle logout
  const handleLogout = () => {
    setIsLoggedIn(false); // Reset login state on logout
  };

  // Function to render the appropriate table based on activeTable state
  const renderTable = () => {
    switch (activeTable) {
      case 'PolicyTable':
        return <PolicyTable />;
      case 'ArchiveTable':
        return <ArchiveTable />;
      case 'UserTable':
        return <UserTable />;
      case 'RoleTable':
        return <RoleTable />;
      case 'RejectedPoliciesTable':
        return <RejectedPoliciesTable />;
      default:
        return <PolicyTable />;
    }
  };

  return (
    <div className="app">
      {/* Render Login component only if not logged in */}
      {!isLoggedIn && <Login handleLogin={handleLogin} />}
      {/* Render main content if logged in */}
      {isLoggedIn && (
        <>
          <Sidebar setActiveTable={setActiveTable} />
          <div className="main-content">
            <Header activeTable={activeTable} onLogout={handleLogout} />
            {renderTable()}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
