// Main.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes, Navigate } from 'react-router-dom';
import App from './components/App';
import Policies from './components/Policies';
import Users from './components/Users';
import Roles from './components/Roles';
import Login from './components/Login'; // Import your Login component here

function Main() {
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = () => {
    setLoggedIn(true);
  };

  const handleLogout = () => {
    setLoggedIn(false);
  };

  const PrivateRoute = ({ element: Element, ...rest }) => {
    return (
      <Route
        {...rest}
        element={loggedIn ? <Element /> : <Navigate to="/login" />}
      />
    );
  };

  return (
    <Router>
      <Routes>
        <Route path="/login" element={<Login onLogin={handleLogin} />} />
        <PrivateRoute path="/" element={App} loggedIn={loggedIn} handleLogout={handleLogout} />
        <PrivateRoute path="/policies" element={Policies} />
        <PrivateRoute path="/users" element={Users} />
        <PrivateRoute path="/roles" element={Roles} />
      </Routes>
    </Router>
  );
}

export default Main;
