import React from 'react';
import shashwat from '../Assert/Shashwat.jpeg';
import { useHistory } from 'react-router-dom'; // Correct import from react-router-dom

const Header = ({ activeTable, onLogout }) => {
  const history = useHistory();

  const getPageTitle = () => {
    switch (activeTable) {
      case 'PolicyTable':
        return 'Policies';
      case 'ArchiveTable':
        return 'Archived Policies';
      case 'UserTable':
        return 'Users';
      case 'RoleTable':
        return 'Roles';
      case 'RejectedPoliciesTable':
        return 'Rejected Policies';
      default:
        return 'Policies';
    }
  };

  const handleLogout = () => {
    onLogout(); // Call the onLogout function passed from App.js
    history.push('/login'); // Redirect to the login page after logout
  };

  return (
    <header className="header">
      <nav className="nav-bar">
        <ul>
          <li className={'active'}>Dashboard</li>
          <li className={'active'}>{getPageTitle()}</li>
        </ul>
      </nav>
      <div className="right-side">
        <div className="search-bar">
          <input type="text" placeholder="Search..." />
        </div>
        <div className="user-profile">
          <button onClick={handleLogout}>Sign Out</button>
          <img src={shashwat} alt="User" /> {/* Replace with actual profile picture URL */}
        </div>
      </div>
    </header>
  );
};

export default Header;
