import React, { useState } from 'react';
import Sidebar from './Sidebar';
import Header from './Header';
import PolicyTable from './PolicyTable';
import ArchiveTable from './ArchiveTable';
import UserTable from './UserTable';
import RoleTable from './RoleTable';
import RejectedPoliciesTable from './RejectedPoliciesTable';
import Login from './Login'; // Import the Login component
import '../Style/App.css'; // Adjust the path as per your project structure

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false); // State to track login status
  const [username, setUsername] = useState(''); // State to hold username
  const [activeTable, setActiveTable] = useState('PolicyTable'); // State to toggle between tables

  // Function to handle login
  const handleLogin = (username, password) => {
    // Check credentials
    if (username === 'Shashwat' && password === 'pass123') {
      setIsLoggedIn(true);
      setUsername(username);
    } else if (username === 'Sahil' && password === '123') {
      setIsLoggedIn(true);
      setUsername(username);
      setActiveTable('PolicyTable'); // Set default table for Sahil
    } else {
      setIsLoggedIn(false);
      setUsername('');
      setActiveTable('PolicyTable'); // Reset to default table
      alert('Invalid username or password. Please try again.');
    }
  };

  // Function to render different tables based on activeTable state
  const renderTable = () => {
    switch (activeTable) {
      case 'PolicyTable':
        return <PolicyTable />;
      case 'ArchiveTable':
        return <ArchiveTable />;
      case 'UserTable':
        return <UserTable />;
      case 'RoleTable':
        return <RoleTable />;
      case 'RejectedPoliciesTable':
        return <RejectedPoliciesTable />;
      default:
        return <PolicyTable />;
    }
  };

  return (
    <div className="app">
      {!isLoggedIn && <Login handleLogin={handleLogin} />}
      {isLoggedIn && (
        <>
          <Sidebar setActiveTable={setActiveTable} />
          <div className="main-content">
            <Header activeTable={activeTable} />
            {renderTable()}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
