import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch, Redirect } from 'react-router-dom';
import Sidebar from './Sidebar';
import Header from './Header';
import PolicyTable from './PolicyTable';
import ArchiveTable from './ArchiveTable';
import UserTable from './UserTable';
import RoleTable from './RoleTable';
import RejectedPoliciesTable from './RejectedPoliciesTable';
import Login from './Login'; // Import the Login component
import '../Style/App.css'; // Ensure the correct relative path

const App = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = (username, password) => {
    // Check credentials (for demonstration purposes)
    if (username === 'Shashwat' && password === 'hello') {
      setIsLoggedIn(true);
    } else {
      setIsLoggedIn(false);
      alert('Invalid username or password. Please try again.');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
  };

  const PrivateRoute = ({ component: Component, ...rest }) => (
    <Route {...rest} render={(props) => (
      isLoggedIn ? <Component {...props} onLogout={handleLogout} /> : <Redirect to="/login" />
    )} />
  );

  return (
    <Router>
      <div className="app">
        {isLoggedIn && <Sidebar />}
        <div className="main-content">
          {isLoggedIn && <Header />}
          <Switch>
            <PrivateRoute path="/policies" component={PolicyTable} />
            <PrivateRoute path="/archived-policies" component={ArchiveTable} />
            <PrivateRoute path="/users" component={UserTable} />
            <PrivateRoute path="/roles" component={RoleTable} />
            <PrivateRoute path="/rejected-policies" component={RejectedPoliciesTable} />
            <Route path="/login" render={() => <Login onLogin={handleLogin} />} />
            <Redirect from="/" to="/policies" />
          </Switch>
        </div>
      </div>
    </Router>
  );
};

export default App;
