// App.js

import React from 'react';
import Sidebar from './Sidebar';
import Header from './Header';
import { Routes, Route, Navigate } from 'react-router-dom';
import '../Style/App.css';
import Policies from './Policies'; // Make sure to import this component
import Users from './Users'; // Make sure to import this component
import Roles from './Roles'; // Make sure to import this component

function App({ loggedIn, handleLogout }) {
  return (
    <div className="app">
      {loggedIn ? (
        <>
          <Header onLogout={handleLogout} />
          <div className="content-container">
            <Sidebar />
            <div className="main-content">
              <Routes>
                <Route path="/" element={<Policies />} />
                <Route path="/policies" element={<Policies />} />
                <Route path="/users" element={<Users />} />
                <Route path="/roles" element={<Roles />} />
                <Route path="*" element={<Navigate to="/" />} />
              </Routes>
            </div>
          </div>
        </>
      ) : (
        <Navigate to="/login" />
      )}
    </div>
  );
}

export default App;
