import React from 'react';

const Sidebar = ({ setActiveTable, userRole }) => {
  return (
    <div className="sidebar">
      {userRole === 'admin' && (
        <>
          <button onClick={() => setActiveTable('PolicyTable')}>Policy Table</button>
          <button onClick={() => setActiveTable('ArchiveTable')}>Archive Table</button>
          <button onClick={() => setActiveTable('UserTable')}>User Table</button>
          <button onClick={() => setActiveTable('RoleTable')}>Role Table</button>
          <button onClick={() => setActiveTable('RejectedPoliciesTable')}>Rejected Policies Table</button>
        </>
      )}
      {userRole === 'creator' && (
        <>
          <button onClick={() => setActiveTable('PolicyList')}>Create Policy</button>
        </>
      )}
    </div>
  );
};

export default Sidebar;
