import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Modal from 'react-modal'

const ApproveRejectPolicyTable = () => {
  const [policies, setPolicies] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [selectedPolicy, setSelectedPolicy] = useState(null);
  const [rejectionReason, setRejectionReason] = useState('');

  useEffect(() => {
    fetchPolicies();
  }, []);

  const fetchPolicies = async () => {
    try {
      const response = await axios.get('http://localhost:8080/policies/ByStatus?status=WIP');
      setPolicies(response.data);
    } catch (error) {
      console.error('Error fetching policies:', error);
    }
  };

  const handleApprove = async (policyId) => {
    try {
      await axios.post(`http://localhost:8080/policies/approve/${policyId}/userId?approve=true`);
      fetchPolicies(); // Refresh the policies after approval
    } catch (error) {
      console.error('Error approving policy:', error);
    }
  };

  const handleReject = (policy) => {
    setSelectedPolicy(policy);
    setIsModalOpen(true);
  };

  const submitRejection = async () => {
    try {
      await axios.post(`http://localhost:8080/policies/approve/${selectedPolicy.id}/userId?approve=false`, {
        rejectionReason
      });
      fetchPolicies(); // Refresh the policies after rejection
      closeModal();
    } catch (error) {
      console.error('Error rejecting policy:', error);
    }
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedPolicy(null);
    setRejectionReason('');
  };

  return (
    <div>
      <h1>Approve or Reject Policies</h1>
      <table>
        <thead>
          <tr>
            <th>Policy ID</th>
            <th>Policy Name</th>
            <th>Creator</th>

          </tr>
        </thead>
        <tbody>
          {policies.map(policy => (
            <tr key={policy.id}>
              <td>{policy.id}</td>
\s
              <td>
                <button onClick={() => handleApprove(policy.id)}>Approve</button>
                <button onClick={() => handleReject(policy)}>Reject</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <Modal
        isOpen={isModalOpen}
        onRequestClose={closeModal}
        contentLabel="Rejection Reason"
      >
        <h2>Rejection Reason</h2>
        <textarea
          value={rejectionReason}
          onChange={(e) => setRejectionReason(e.target.value)}
        />
        <button onClick={submitRejection}>Submit</button>
        <button onClick={closeModal}>Cancel</button>
      </Modal>
    </div>
  );
};

export default ApproveRejectPolicyTable;
