import React from 'react';
import { NavLink } from 'react-router-dom'; // Use NavLink for active styling
import shashwat from '../Assert/Shashwat.jpeg';
import '../Style/Header.css';

const Header = ({ onLogout }) => {
  const handleSignOut = () => {
    // Perform any additional cleanup or logout operations here if needed
    onLogout();
  };

  return (
    <header className="header">
      <nav className="nav-bar">
        <ul>
          <li><NavLink exact to="/" activeClassName="active" className="link">Dashboard</NavLink></li>
          <li><NavLink to="/policies" activeClassName="active" className="link">Archive Policy</NavLink></li>
          <li><NavLink to="/users" activeClassName="active" className="link">Users</NavLink></li>
          <li><NavLink to="/roles" activeClassName="active" className="link">Roles</NavLink></li>
        </ul>
      </nav>
      <div className="right-side">
        <div className="search-bar">
          <input type="text" placeholder="Search..." />
        </div>
        <div className="user-profile">
          <button onClick={handleSignOut}>Sign Out</button>
          <img src={shashwat} alt="User" />
        </div>
      </div>
    </header>
  );
};

export default Header;
