import React, { useState } from 'react';
import '../Style/Login.css'; // Import CSS for Login page styling
import logo from '../Assert/logo1.png'; // Import logo image

const Login = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');

  const handleLogin = () => {
    // Hardcoded username and password check
    if (username === 'Shashwat' && password === 'pass123') {
      onLogin(true); // Notify parent component (App.js) about successful login
    } else {
      alert('Invalid username or password');
    }
  };

  return (
    <div className="login-page">
      <div className="left-side">
        <img src={logo} alt="Login Visual" />
      </div>
      <div className="right-side">
        <div className="login-form">
          <h2>Login</h2>
          <div className="form-input">
            <input
              type="text"
              placeholder="Username"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
          </div>
          <div className="form-input">
            <input
              type="password"
              placeholder="Password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
          </div>
          <button onClick={handleLogin}>Login</button>
        </div>
      </div>
    </div>
  );
};

export default Login;
