import React, { useState } from 'react';
import Sidebar from './Sidebar';
import Header from './Header';
import PolicyTable from './PolicyTable';
import ArchiveTable from './ArchiveTable';
import UserTable from './UserTable';
import RoleTable from './RoleTable';
import RejectedPoliciesTable from './RejectedPoliciesTable';
import Login from './Login';
import '../Style/App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [activeTable, setActiveTable] = useState('PolicyTable');

  const handleLogin = (username, password) => {
    if (username === 'Shashwat' && password === 'pass123') {
      setIsLoggedIn(true);
      setUsername(username);
    } else if (username === 'Sahil' && password === '123') {
      setIsLoggedIn(true);
      setUsername(username);
      setActiveTable('PolicyTable');
    } else {
      setIsLoggedIn(false);
      setUsername('');
      setActiveTable('PolicyTable');
      alert('Invalid username or password. Please try again.');
    }
  };

  const renderTable = () => {
    switch (activeTable) {
      case 'PolicyTable':
        return <PolicyTable />;
      case 'ArchiveTable':
        return <ArchiveTable />;
      case 'UserTable':
        return <UserTable />;
      case 'RoleTable':
        return <RoleTable />;
      case 'RejectedPoliciesTable':
        return <RejectedPoliciesTable />;
      default:
        return <PolicyTable />;
    }
  };

  return (
    <div className="app">
      {!isLoggedIn && <Login handleLogin={handleLogin} />}
      {isLoggedIn && (
        <>
          <Sidebar setActiveTable={setActiveTable} />
          <div className="main-content">
            <Header activeTable={activeTable} />
            {renderTable()}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
