import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Switch } from 'react-router-dom';
import App from './components/App';
import Policies from './components/Policies'; // Create this component
import Users from './components/Users'; // Create this component
import Roles from './components/Roles'; // Create this component

function Main() {
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = (status) => {
    setLoggedIn(status);
  };

  const handleLogout = () => {
    setLoggedIn(false);
  };

  return (
    <Router>
      <Switch>
        <Route exact path="/">
          <App loggedIn={loggedIn} handleLogin={handleLogin} handleLogout={handleLogout} />
        </Route>
        <Route path="/policies">
          <Policies />
        </Route>
        <Route path="/users">
          <Users />
        </Route>
        <Route path="/roles">
          <Roles />
        </Route>
      </Switch>
    </Router>
  );
}

export default Main;
