import React, { useState } from 'react';
import { sortData } from '../utils/sort'; // Import your sorting function

const UserTable = () => {
  const users = [
    { name: 'Shashwat sirjam', role: 'Admin', email: 'Shashwat@example.com', lastLogin: '2022-10-01', actions: ['Edit', 'Delete'] },
    { name: 'Mansi', role: 'User', email: 'Mansi@example.com', lastLogin: '2022-09-28', actions: ['Edit', 'Delete'] },
    { name: 'Sourabh', role: 'Admin', email: 'Sourabh@example.com', lastLogin: '2022-10-01', actions: ['Edit', 'Delete'] },
    { name: 'purneeth', role: 'User', email: 'purneeth@example.com', lastLogin: '2022-09-28', actions: ['Edit', 'Delete'] },
    { name: 'Shashwat sirjam', role: 'Admin', email: 'Shashwat@example.com', lastLogin: '2022-10-01', actions: ['Edit', 'Delete'] },
    { name: 'Mansi', role: 'User', email: 'Mansi@example.com', lastLogin: '2022-09-28', actions: ['Edit', 'Delete'] },
    { name: 'Sourabh', role: 'Admin', email: 'Sourabh@example.com', lastLogin: '2022-10-01', actions: ['Edit', 'Delete'] },
    { name: 'purneeth', role: 'User', email: 'purneeth@example.com', lastLogin: '2022-09-28', actions: ['Edit', 'Delete'] },
    // Add more users as needed
  ];

  const [sortedUsers, setSortedUsers] = useState([]);
  const [sortBy, setSortBy] = useState('name');
  const [sortDirection, setSortDirection] = useState('asc');

  // Function to handle sorting
  const handleSort = (field) => {
    const direction = sortBy === field && sortDirection === 'asc' ? 'desc' : 'asc';
    setSortBy(field);
    setSortDirection(direction);
    setSortedUsers(sortData(users, field, direction));
  };

  // Initial sorting on component mount (optional)
  useState(() => {
    setSortedUsers([...users]); // Sort users initially if needed
  }, []);

  // Determine which array to display (sorted or original)
  const usersToDisplay = sortedUsers.length > 0 ? sortedUsers : users;

  return (
    <div className="policy-table">
      <h2>User Management Overview</h2>
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort('name')}>Name</th>
            <th onClick={() => handleSort('role')}>Role</th>
            <th onClick={() => handleSort('email')}>Email</th>
            <th onClick={() => handleSort('lastLogin')}>Last Login</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {usersToDisplay.map((user, index) => (
            <tr key={index}>
              <td>{user.name}</td>
              <td>{user.role}</td>
              <td>{user.email}</td>
              <td>{user.lastLogin}</td>
              <td>
                {user.actions.map((action, idx) => (
                  <button key={idx}>{action}</button>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserTable;
