import React from 'react';

const PolicyTable = () => {
  const policies = [
    { name: 'Access Control', description: 'Control user access', createdBy: 'Admin1', lastUpdated: '2022-10-01', actions: ['View'] },
    { name: 'Data Encryption', description: 'Encrypt sensitive data', createdBy: 'Admin2', lastUpdated: '2022-09-28', actions: ['Edit'] },
    { name: 'User Permissions', description: 'Manage user entitlements', createdBy: 'Admin3', lastUpdated: '2022-09-30', actions: ['Delete'] },
    { name: 'Audit Trail', description: 'Track user actions', createdBy: 'Admin1', lastUpdated: '2022-10-02', actions: ['View'] },
    { name: 'Password Policy', description: 'Enforce password rules', createdBy: 'Admin2', lastUpdated: '2022-10-05', actions: ['Edit'] },
    { name: 'Backup Policy', description: 'Regular data backups', createdBy: 'Admin3', lastUpdated: '2022-10-03', actions: ['Delete'] },
    { name: 'Security Updates', description: 'Apply system patches', createdBy: 'Admin1', lastUpdated: '2022-10-04', actions: ['View'] },
  ];

  return (
    <div className="policy-table">
      <h2>Policy Management Overview</h2>
      <table>
        <thead>
          <tr>
            <th>Policy Name</th>
            <th>Description</th>
            <th>Created By</th>
            <th>Last Updated</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {policies.map((policy, index) => (
            <tr key={index}>
              <td>{policy.name}</td>
              <td>{policy.description}</td>
              <td>{policy.createdBy}</td>
              <td>{policy.lastUpdated}</td>
              <td>
                {policy.actions.map((action, idx) => (
                  <button key={idx}>{action}</button>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PolicyTable;
