// Header.js

import React from 'react';
import { NavLink } from 'react-router-dom';
import shashwat from '../assets/Shashwat.jpeg'; // Adjust path for your image
import '../styles/Header.css'; // Adjust path for your styles

const Header = ({ onLogout }) => {
  const handleSignOut = () => {
    // Perform any additional cleanup or logout operations here if needed
    onLogout();
  };

  return (
    <header className="header">
      <nav className="nav-bar">
        <ul>
          <li>
            <NavLink to="/" end activeClassName="active">Dashboard</NavLink>
          </li>
          <li>
            <NavLink to="/policies" activeClassName="active">Archive Policy</NavLink>
          </li>
          <li>
            <NavLink to="/users" activeClassName="active">Users</NavLink>
          </li>
          <li>
            <NavLink to="/roles" activeClassName="active">Roles</NavLink>
          </li>
        </ul>
      </nav>
      <div className="right-side">
        <div className="search-bar">
          <input type="text" placeholder="Search..." />
        </div>
        <div className="user-profile">
          <button onClick={handleSignOut}>Sign Out</button>
          <img src={shashwat} alt="User" />
        </div>
      </div>
    </header>
  );
};

export default Header;
