import React from 'react';
import '../Style/App.css';
import '../Style/Policies.css';

const UserTable = () => {
  const users = [
    { name: 'John Doe', role: 'Admin', email: 'john@example.com', lastLogin: '2022-10-01', actions: ['Edit', 'Delete'] },
    { name: 'Jane Smith', role: 'User', email: 'jane@example.com', lastLogin: '2022-09-28', actions: ['Edit', 'Delete'] },
    // Add more users as needed
  ];

  return (
    <div className="policy-table"> {/* Use the same class as PolicyTable */}
      <h2>User Management Overview</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Role</th>
            <th>Email</th>
            <th>Last Login</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={index}>
              <td>{user.name}</td>
              <td>{user.role}</td>
              <td>{user.email}</td>
              <td>{user.lastLogin}</td>
              <td>
                {user.actions.map((action, idx) => (
                  <button key={idx}>{action}</button>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserTable;
