import React from 'react';
import '../Style/App.css';
import '../Style/Policies.css';

const ArchiveTable = () => {
  const archivedPolicies = [
    { name: 'Archived Policy 1', description: 'Description 1', createdBy: 'User1', lastUpdated: '2022-10-01', actions: ['View'] },
    { name: 'Archived Policy 2', description: 'Description 2', createdBy: 'User2', lastUpdated: '2022-10-02', actions: ['View'] },
    // Add more archived policies as needed
  ];

  return (
    <div className="policy-table"> {/* Use the same class as PolicyTable */}
      <h2>Archived Policies Overview</h2>
      <div className="archive-policies">
        <div className="filter-options">
          <label htmlFor="filter-date">Filter by Date</label>
          <input type="date" id="filter-date" name="filter-date" />
          <button>View</button>
        </div>
      </div>
      <table>
        <thead>
          <tr>
            <th>Policy Name</th>
            <th>Description</th>
            <th>Created By</th>
            <th>Last Updated</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {archivedPolicies.map((policy, index) => (
            <tr key={index}>
              <td>{policy.name}</td>
              <td>{policy.description}</td>
              <td>{policy.createdBy}</td>
              <td>{policy.lastUpdated}</td>
              <td>
                {policy.actions.map((action, idx) => (
                  <button key={idx}>{action}</button>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ArchiveTable;
