import React, { useState } from 'react';
import '../Style/App.css';
import { sortData } from '../utils/sort';

const UserTable = () => {
  const [users, setUsers] = useState([
    { name: 'Admin', description: 'Full access to all resources', createdBy: 'System', createdOn: '2022-01-01', actions: ['Edit', 'Delete'] },
    { name: 'User', description: 'Limited access to resources', createdBy: 'System', createdOn: '2022-01-01', actions: ['Edit', 'Delete'] },
    // Add more roles as needed
  ]);
  const [sortBy, setSortBy] = useState('name');
  const [sortDirection, setSortDirection] = useState('asc');

  const handleSort = (field) => {
    const direction = sortBy === field && sortDirection === 'asc' ? 'desc' : 'asc';
    setSortBy(field);
    setSortDirection(direction);
    setUsers(sortData(users, field, direction));
  };

  return (
    <div className="user-table">
      <h2>User Management Overview</h2>
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort('name')}>Name</th>
            <th onClick={() => handleSort('email')}>Email</th>
            <th onClick={() => handleSort('role')}>Role</th>
            <th onClick={() => handleSort('createdDate')}>Created Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {users.map((user, index) => (
            <tr key={index}>
              <td>{user.name}</td>
              <td>{user.email}</td>
              <td>{user.role}</td>
              <td>{user.createdDate}</td>
              <td>
                <button>View</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default UserTable;
