import { NavLink } from 'react-router-dom';
import React from 'react';
import { Link } from 'react-router-dom';
import shashwat from '../Assert/Shashwat.jpeg'
import '../Style/Header.css';
const Header = ({ onLogout }) => {
  const handleSignOut = () => {
    // Perform any additional cleanup or logout operations here if needed
    onLogout();
  };

  return (
    <header className="header">
      <nav className="nav-bar">
        <ul>
        <li>
                        <NavLink to="/" className={({ isActive }) => (isActive ? 'active' : '')}>Dashboard</NavLink>
                    </li>
                    <li>
                        <NavLink to="/policies" className={({ isActive }) => (isActive ? 'active' : '')}>Archive Policy</NavLink>
                    </li>
                    <li>
                        <NavLink to="/users" className={({ isActive }) => (isActive ? 'active' : '')}>Users</NavLink>
                    </li>
                    <li>
                        <NavLink to="/roles" className={({ isActive }) => (isActive ? 'active' : '')}>Roles</NavLink>
                    </li>
        </ul>
      </nav>
      <div className="right-side">
        <div className="search-bar">
          <input type="text" placeholder="Search..." />
        </div>
        <div className="user-profile">
          <button onClick={handleSignOut}>Sign Out</button>
          <img src={shashwat} alt="User" />
        </div>
      </div>
    </header>
  );
};

export default Header;
