import React from 'react';

const Sidebar = ({ setActiveTable, role }) => {
  return (
    <div className="sidebar">
      <ul>
        <li onClick={() => setActiveTable('PolicyTable')}>Policies</li>
        {role === 'admin' && (
          <>
            <li onClick={() => setActiveTable('ArchiveTable')}>Archived Policies</li>
            <li onClick={() => setActiveTable('UserTable')}>Users</li>
            <li onClick={() => setActiveTable('RoleTable')}>Roles</li>
            <li onClick={() => setActiveTable('RejectedPoliciesTable')}>Rejected Policies</li>
          </>
        )}
      </ul>
    </div>
  );
};

export default Sidebar;
