import React, { useState } from 'react';
import Sidebar from './Sidebar';
import Header from './Header';
import PolicyTable from './PolicyTable';
import ArchiveTable from './ArchiveTable';
import UserTable from './UserTable';
import RoleTable from './RoleTable';
import RejectedPoliciesTable from './RejectedPoliciesTable';
import '../Style/App.css'; // Ensure the correct relative path

function App() {
  const [activeTable, setActiveTable] = useState('PolicyTable'); // State to toggle between tables

  const renderTable = () => {
    switch (activeTable) {
      case 'PolicyTable':
        return <PolicyTable setActiveTable={setActiveTable} />;
      case 'ArchiveTable':
        return <ArchiveTable />;
      case 'UserTable':
        return <UserTable />;
      case 'RoleTable':
        return <RoleTable />;
      case 'RejectedPoliciesTable':
        return <RejectedPoliciesTable />;
      default:
        return <PolicyTable />;
    }
  };

  return (
    <div className="app">
      <Sidebar setActiveTable={setActiveTable} />
      <div className="main-content">
        <Header activeTable={activeTable} />
        {renderTable()}
      </div>
    </div>
  );
}

export default App;
