import React from 'react';

const Header = ({ activeTable }) => {

  return (
    <header className="header">
      <nav className="nav-bar">
        <ul>
          <li className={activeTable === 'PolicyTable' ? 'active' : ''}>Dashboard</li>
          <li className={activeTable === 'PolicyTable' ? 'active' : ''}>Policies</li>
          <li className={activeTable === 'UserTable' ? 'active' : ''}>Users</li>
          <li className={activeTable === 'RoleTable' ? 'active' : ''}>Roles</li>
        </ul>
      </nav>
      <div className="right-side">
        <div className="search-bar">
          <input type="text" placeholder="Search..." />
        </div>
        <div className="user-profile">
          <button>Sign Out</button>
          <img src="profile-pic-url" alt="User" /> {/* Replace with actual profile picture URL */}
        </div>
      </div>
    </header>
  );
};

export default Header;
