import React from 'react';
import '../Style/PolicyList.css'; // Create and import CSS for styling

const PolicyList = () => {
  const policies = [
    { name: 'India Country Rule' },
    { name: 'US Country Rule' },
    // Add more policies here
  ];

  return (
    <div className="policy-list-container">
      <h2>Policy Management</h2>
      <button className="create-policy-button">Create Policy</button>
      <h3>Policy List</h3>
      <table className="policy-table">
        <thead>
          <tr>
            <th>Policy Name</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {policies.map((policy, index) => (
            <tr key={index}>
              <td>{policy.name}</td>
              <td>
                <button>Edit</button>
                <button>Approve</button>
                <button>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PolicyList;
