import React from 'react';

const Sidebar = ({ setActiveTable }) => {
  const handleClick = (table) => {
    setActiveTable(table);
  };

  return (
    <div className="sidebar">
      <div className="logo">SCB BANK</div>
      <ul>
        <li onClick={() => handleClick('PolicyTable')} className="active">Dashboard</li>
        <li onClick={() => handleClick('PolicyTable')}>Policies</li>
        <li onClick={() => handleClick('UserTable')}>Users</li>
        <li onClick={() => handleClick('RoleTable')}>Roles</li>
        <li onClick={() => handleClick('ArchiveTable')}>Archived Policies</li>
        <li onClick={() => handleClick('RejectedPoliciesTable')}>Rejected Policies</li>
      </ul>
    </div>
  );
};

export default Sidebar;
