import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { sortData } from '../Util/sort';

const ArchiveTable = () => {
  const [archivedPolicies, setArchivedPolicies] = useState([]);
  const [filteredPolicies, setFilteredPolicies] = useState([]);
  const [sortBy, setSortBy] = useState('name');
  const [sortDirection, setSortDirection] = useState('asc');

  useEffect(() => {
    // Fetch archived policies from backend
    axios.get('http://localhost:8080/policies/archive')
      .then(response => {
        console.log('Fetched archived policies data:', response.data);
        setArchivedPolicies(response.data);
        setFilteredPolicies(response.data);
      })
      .catch(error => {
        console.error('There was an error fetching the archived policies data!', error);
      });
  }, []);

  const handleSort = (field) => {
    const direction = sortBy === field && sortDirection === 'asc' ? 'desc' : 'asc';
    setSortBy(field);
    setSortDirection(direction);
    setFilteredPolicies(sortData(archivedPolicies, field, direction));
  };

  const handleFilterByDate = () => {
    const selectedDate = document.getElementById('filter-date-archive').value;
    const filtered = archivedPolicies.filter(policy => policy.lastUpdated === selectedDate);
    setFilteredPolicies(filtered);
  };

  const handleResetFilter = () => {
    setFilteredPolicies([]);
  };

  const policiesToDisplay = filteredPolicies.length > 0 ? filteredPolicies : archivedPolicies;

  return (
    <div className="policy-table"> {/* Use the same class as PolicyTable */}
      <h2>Archived Policies Overview</h2>
      <div className="archive-policies">
        <div className="filter-options">
          <label htmlFor="filter-date-archive">Filter by Date</label>
          <input type="date" id="filter-date-archive" name="filter-date-archive" />
          <button onClick={handleFilterByDate}>View</button>
          <button onClick={handleResetFilter}>Reset Filter</button>
        </div>
      </div>
      <table>
        <thead>
          <tr>
            <th onClick={() => handleSort('name')}>Policy Name</th>
            <th onClick={() => handleSort('description')}>Description</th>
            <th onClick={() => handleSort('createdBy')}>Created By</th>
            <th onClick={() => handleSort('lastUpdated')}>Last Updated</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {policiesToDisplay.map((policy, index) => (
            <tr key={index}>
              <td>{policy.dataPolicyName}</td>
              <td>{policy.dataPolicyName}</td>
              <td>{policy.dataPolicyName}</td>
              <td>{policy.dataPolicyName}</td>
              <td>
                {policy.actions.map((action, idx) => (
                  <button key={idx}>{action}</button>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default ArchiveTable;
