import React from 'react';
import '../Style/App.css';
import '../Style/Policies.css';

const PolicyTable = ({ setActiveTable }) => {
  const policies = [
    { name: 'Access Control', description: 'Control user access', createdBy: 'Mansi', lastUpdated: '2022-10-01', actions: ['View'] },
    { name: 'Data Encryption', description: 'Encrypt sensitive data', createdBy: 'Saurabh', lastUpdated: '2022-09-28', actions: ['View'] },
    { name: 'User Permissions', description: 'Manage user entitlements', createdBy: 'Praneeth', lastUpdated: '2022-09-30', actions: ['View'] },
    { name: 'Audit Trail', description: 'Track user actions', createdBy: 'Mansi', lastUpdated: '2022-10-02', actions: ['View'] },
    { name: 'Password Policy', description: 'Enforce password rules', createdBy: 'Saurabh', lastUpdated: '2022-10-05', actions: ['View'] },
    { name: 'Backup Policy', description: 'Regular data backups', createdBy: 'Praneeth', lastUpdated: '2022-10-03', actions: ['View'] },
    { name: 'Security Updates', description: 'Apply system patches', createdBy: 'Mansi', lastUpdated: '2022-10-04', actions: ['View'] },
    { name: 'Access Control', description: 'Control user access', createdBy: 'Mansi', lastUpdated: '2022-10-01', actions: ['View'] },
    { name: 'Data Encryption', description: 'Encrypt sensitive data', createdBy: 'Saurabh', lastUpdated: '2022-09-28', actions: ['View'] },
    { name: 'User Permissions', description: 'Manage user entitlements', createdBy: 'Praneeth', lastUpdated: '2022-09-30', actions: ['View'] },
    { name: 'Audit Trail', description: 'Track user actions', createdBy: 'Mansi', lastUpdated: '2022-10-02', actions: ['View'] },
    { name: 'Password Policy', description: 'Enforce password rules', createdBy: 'Saurabh', lastUpdated: '2022-10-05', actions: ['View'] },
    { name: 'Backup Policy', description: 'Regular data backups', createdBy: 'Praneeth', lastUpdated: '2022-10-03', actions: ['View'] },
    { name: 'Security Updates', description: 'Apply system patches', createdBy: 'Mansi', lastUpdated: '2022-10-04', actions: ['View'] },
  ];

  const handleViewRejectedPolicies = () => {
    setActiveTable('RejectedPoliciesTable');
  };

  return (
    <div className="policy-table">
      <h2>Policy Management Overview</h2>

      <div className="archive-policies">
          <div className="filter-options">
            <label htmlFor="filter-date">Filter by Date</label>
            <input type="date" id="filter-date" name="filter-date" />
            <button>View</button>
            <button onClick={handleViewRejectedPolicies}>View Rejected Policies</button>
          </div>
      </div>

      <table>
        <thead>
          <tr>
            <th>Policy Name</th>
            <th>Description</th>
            <th>Created By</th>
            <th>Last Updated</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {policies.map((policy, index) => (
            <tr key={index}>
              <td>{policy.name}</td>
              <td>{policy.description}</td>
              <td>{policy.createdBy}</td>
              <td>{policy.lastUpdated}</td>
              <td>
                {policy.actions.map((action, idx) => (
                  <button key={idx}>{action}</button>
                ))}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default PolicyTable;
