// Main.js

import React, { useState } from 'react';
import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import App from './components/App';
import Policies from './components/Policies'; // Import Policies component
import Users from './components/Users'; // Import Users component
import Roles from './components/Roles'; // Import Roles component

function Main() {
  const [loggedIn, setLoggedIn] = useState(false);

  const handleLogin = (status) => {
    setLoggedIn(status);
  };

  const handleLogout = () => {
    setLoggedIn(false);
  };

  return (
    <Router>
      <Routes>
        <Route
          path="/"
          element={<App loggedIn={loggedIn} handleLogin={handleLogin} handleLogout={handleLogout} />}
        />
        <Route path="/policies" element={<Policies />} /> {/* Route for Policies component */}
        <Route path="/users" element={<Users />} />
        <Route path="/roles" element={<Roles />} />
      </Routes>
    </Router>
  );
}

export default Main;
