import React, { useState } from 'react';
import Sidebar from './Sidebar';
import Header from './Header';
import PolicyTable from './PolicyTable';
import ArchiveTable from './ArchiveTable';
import UserTable from './UserTable';
import RoleTable from './RoleTable';
import RejectedPoliciesTable from './RejectedPoliciesTable';
import PolicyList from './PolicyList'; // Import the new PolicyList component
import Login from './Login';
import '../Style/App.css';

function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [activeTable, setActiveTable] = useState('PolicyTable');
  const [user, setUser] = useState(null);

  const handleLogin = (userInfo) => {
    setIsLoggedIn(true);
    setUser(userInfo);
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUser(null);
  };

  const renderTable = () => {
    if (user.role === 'creator') {
      return <PolicyList />;
    }
    switch (activeTable) {
      case 'PolicyTable':
        return <PolicyTable />;
      case 'ArchiveTable':
        return <ArchiveTable />;
      case 'UserTable':
        return <UserTable />;
      case 'RoleTable':
        return <RoleTable />;
      case 'RejectedPoliciesTable':
        return <RejectedPoliciesTable />;
      default:
        return <PolicyTable />;
    }
  };

  return (
    <div className="app">
      {!isLoggedIn && <Login handleLogin={handleLogin} />}
      {isLoggedIn && (
        <>
          <Sidebar setActiveTable={setActiveTable} userRole={user.role} />
          <div className="main-content">
            <Header activeTable={activeTable} user={user} handleLogout={handleLogout} />
            {renderTable()}
          </div>
        </>
      )}
    </div>
  );
}

export default App;
