import React from 'react';
import { useNavigate } from 'react-router-dom';

const Policies = () => {
  const navigate = useNavigate();

  const handleNotLoggedIn = () => {
    navigate('/', { replace: true });
  };

  return (
    <div className="policies-container">
      <h2>Archive Policies</h2>
      <table>
          <thead>
            <tr>
              <th>Policy Name</th>
              <th>Version</th>
              <th>Creator</th>
              <th>Created On</th>
              <th>Decommissioned On</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>Policy A</td>
              <td>1.0</td>
              <td>John Doe</td>
              <td>2022-09-15</td>
              <td>N/A</td>
            </tr>
            <tr>
              <td>Policy B</td>
              <td>2.5</td>
              <td>Jane Smith</td>
              <td>2022-08-20</td>
              <td>2022-10-01</td>
            </tr>
            {/* Add more rows as needed */}
          </tbody>
        </table>
        <div className="filter-options">
          <label htmlFor="filter-date">Filter by Date</label>
          <input type="date" id="filter-date" name="filter-date" />
          <button>View</button>
          <button>View Rejected Policies</button>
        </div>
      {!localStorage.getItem('loggedIn') && handleNotLoggedIn()}
    </div>
  );
};

export default Policies;
