import React, { useState } from 'react';
import Sidebar from './Sidebar';
import Header from './Header';
import PolicyTable from './PolicyTable';
import ArchiveTable from './ArchiveTable'; // Import the ArchiveTable component
import '../Style/App.css'; // Ensure the correct relative path

function App() {
  const [showArchive, setShowArchive] = useState(false); // State to toggle between tables

  return (
    <div className="app">
      <Sidebar setShowArchive={setShowArchive} />
      <div className="main-content">
        <Header />
        {showArchive ? <ArchiveTable /> : <PolicyTable />}
      </div>
    </div>
  );
}

export default App;
