// App.js

import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Sidebar from './Sidebar';
import Header from './Header';
import PolicyTable from './PolicyTable';
import Login from './Login';
import Policies from './Policies'; // Import Policies component
import Users from './Users'; // Import Users component
import Roles from './Roles'; // Import Roles component
import '../style/App.css'; // Adjust path as needed for your styles

function App({ loggedIn, handleLogout }) {
  return (
    <div className="app">
      {!loggedIn ? (
        <Navigate to="/login" />
      ) : (
        <>
          <Sidebar />
          <div className="main-content">
            <Header onLogout={handleLogout} />
            <Routes>
              <Route path="/" element={<PolicyTable />} />
              <Route path="/policies" element={<Policies />} />
              <Route path="/users" element={<Users />} />
              <Route path="/roles" element={<Roles />} />
            </Routes>
          </div>
        </>
      )}
    </div>
  );
}

export default App;
